package teststraining;

public class StringTestTraining {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String Auto = "Automation";
		System.out.println(Auto);
		System.out.println("Automation");
		
		String Tool = "Selenium";
		System.out.println(Tool);
		System.out.println("Tool");
		
		System.out.println(" ");
		
		System.out.println(Auto + Tool);
		System.out.println(Auto+" "+Tool);
		
		int a = 10;
		int b = 20;
		
		System.out.println(a+b+Auto+Tool);
		System.out.println(a+" "+Auto+" "+b+" "+Tool);
		System.out.println(Auto+" "+a+b+" "+Tool);
		System.out.println(Auto+" "+(a+b)+" "+Tool);
		System.out.println(Auto+" "+(a+b)+" "+Tool + a + b);
		
		
	}

}
